<?php    add_action('admin_menu', 'isr_menu_pages');    function isr_menu_pages(): void    {        add_submenu_page('options-general.php', 'ISR Settings',            'ISR Settings', 'manage_options', 'isr-settings', 'isr_settings');    }    function isr_settings(): void    {        global $wpdb;        $table = $wpdb->usermeta;        $query = "SELECT DISTINCT 'meta_value' AS 'name' FROM $table WHERE 'meta_key' = 'agent_position'";        $positions = $wpdb->get_results($query);        if (isset($_POST['set-forms'])) {            update_option('ci_form_id', $_POST['ci-form-id']);            update_option('wcn_form_id', $_POST['wcn-form-id']);            update_option('preset_appt_days', $_POST['prstdays']);            update_option('hidden_positions', $_POST['hidepos']);        }        // Why are we echoing HTML here? Is this file being used as a template page?        ?>        <h2 style="margin-top: 60px;">ISR Settings</h2>        <hr/>        <div style="height: 30px;"></div>        <?php        $presetDays = get_option('preset_appt_days');        $hiddenPositions = get_option('hidden_positions');        if ($presetDays == null || sizeof($presetDays) == 0) {            $presetDays = array();        }        if ($hiddenPositions == null || sizeof($hiddenPositions) == 0) {            $hiddenPositions = array();        }        ?>        <form method="POST" action="">            <label for="ci-form-id">Calendar Invite Form</label><br/>            <input type="text" style="width: 300px;" id="ci-form-id" name="ci-form-id"                   value="<?php echo get_option('ci_form_id'); ?>"/>            <br/><br/>            <label for="wcn-form-id">WCN Submission / Creation Form</label><br/>            <input type="text" style="width: 300px;" id="wcn-form-id" name="wcn-form-id"                   value="<?php echo get_option('wcn_form_id'); ?>"/>            <br/><br/>            <label>Preset Appointment Days</label><br/>            <label for="prstSunday">                <input name="prstdays[]" <?php echo in_array('Sun', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Sun" class="preset-appt-day" id="prstSunday"/>Sunday&nbsp;            </label>            <label for="prstMonday">                <input name="prstdays[]" <?php echo in_array('Mon', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Mon" class="preset-appt-day" id="prstMonday"/>Monday&nbsp;            </label>            <label for="prstTuesday">                <input name="prstdays[]" <?php echo in_array('Tue', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Tue" class="preset-appt-day" id="prstTuesday"/>Tuesday&nbsp;            </label>            <label>                <input name="prstdays[]" <?php echo in_array('Wed', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Wed" class="preset-appt-day" id="prstWednesday"/>Wednesday&nbsp;&nbsp;&nbsp;            </label>            <label for="prstThursday">                <input name="prstdays[]" <?php echo in_array('Thu', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Thu" class="preset-appt-day" id="prstThursday"/>Thursday&nbsp;            </label>            <label for="prstFriday">                <input name="prstdays[]" <?php echo in_array('Fri', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Fri" class="preset-appt-day" id="prstFriday"/>Friday&nbsp;            </label>            <label for="prstSaturday">                <input name="prstdays[]" <?php echo in_array('Sat', $presetDays) ? 'checked' : ''; ?> type="checkbox"                       value="Sat" class="preset-appt-day" id="prstSaturday"/>Saturday&nbsp;            </label>            <br/><br/>            <?php                $positionsCheckboxes = sizeof($positions) > 0;                if ($positionsCheckboxes) {                    ?>                    <label>Hide Positions From Analysis</label><br/>                    <?php                    foreach ($positions as $position) { ?>                            <label>                                <input <?php echo in_array(strtolower($position->name), array_map('strtolower', $hiddenPositions)) ? 'checked' : ''; ?>                                        type="checkbox" value="<?php echo $position->name; ?>"                                        name="hidepos[]"/><?php echo ucwords(strtolower($position->name)); ?>&nbsp;&nbsp;                            </label>                    <?php }                    ?>                    <br/><br/>                    <?php                }            ?>            <button type="submit" class="button button primary" name="set-forms">Set Values</button>        </form>        <?php    }?>